import "./Reset.css";
import "./Style.css";
import inicio from "./forca0.png";
import alfabeto from "./Letras";
import palavras from "./Palavras";

export default function TelaI(){
    return (<div>        
    <div className="faixaSuperior">
        <img src={inicio} alt="" />
        <button onClick={()=>EscolherPalavra}>Escolher Palavra</button>
    </div>  
    <ul className="teclado"><InserirLetras /></ul>      
    <div className="chute"> <label>JÃ¡ sei a palavra!</label><input></input><button>Chutar!</button> </div>
     </div>)
}
function InserirLetras(){
    return (<>
        {alfabeto.map((l, index) => <li key={index}>{l}</li>)}
   </>)
}
function EscolherPalavra(){
    let palavraEscolhida;
    let aleatorio;
    do {
      aleatorio = Math.floor(Math.random()*100);
      console.log(aleatorio);
    } while(aleatorio > 80);
    palavraEscolhida = palavras[aleatorio];
    alert(palavraEscolhida)    
    }
