import React from "react";
import "./Reset.css";
import "./Style.css";
import inicio from "./forca0.png";
import alfabeto from "./Letras";
import palavras from "./Palavras";

export default function Forca(){
    const [escolhida, setEscolhida] = React.useState("Palavra vem ai");
    function EscolherPalavra(){
    let palavraEscolhida;
    let aleatorio = Math.floor(Math.random() * 232); //232 é a quantidade de itens no array palavras +1 por causa do Math 
    palavraEscolhida = palavras[aleatorio]; //Escolhe uma palavra do array palavras
    setEscolhida(palavraEscolhida)
    RenderizaEspacos(palavraEscolhida)
}   
    function RenderizaEspacos(p){
        let lacunas= p.split();
        alert(lacunas)
    }
    //Renderiza as letras do alfabeto
    function InserirLetras(){
    return (<>
        {alfabeto.map((l, index) => <li key={index}>{l}</li>)}
    </>)
}   //Renderiza a tela
    return (<div>
    <div className="faixaSuperior">
        <img src={inicio} alt="" />
        <button onClick={EscolherPalavra}>Escolher Palavra</button>
        <p>{escolhida}</p>
    </div>  
    <ul className="teclado"><InserirLetras /></ul>      
    <div className="chute"> <label>Já sei a palavra!</label><input></input><button>Chutar!</button> </div>
    </div>)
}
