import {useState} from "react";
import "./Reset.css";
import "./Style.css";
import forca0 from "./forca0.png";

import alfabeto from "./Letras";
import palavras from "./Palavras";

export default function Forca(){
    let palavraEscolhida;
    let pDividida;
    let lacunas = [];
    let palavraFoiEscolhida = false;
    const [escolhida, setEscolhida] = useState(palavraEscolhida);
    const [letraS, setLetrasCertas] = useState([]);
    const [jogavel, setJogo] = useState("desabilitado");
    const [chuteP, setChuteP] = useState("");        
    const [iniciarJogo, setInicioJogo] = useState(palavraFoiEscolhida);
    //const [arrayC, setVerificarLetra] = useState(pDividida)

    //Escolhe a palavra e habilita o jogo
    function EscolherPalavra(){        
        palavraEscolhida = palavras[Math.floor(Math.random() * palavras.length)]; //Escolhe uma palavra do array palavras
        let inicio = true;
        RenderizaLacunas(palavraEscolhida)
        setInicioJogo(inicio)
        setJogo("habilitado")}

    //Renderizar as lacunas
    function RenderizaLacunas(palavraEscolhida){
        pDividida = [...palavraEscolhida]
            for(let i =0; i < pDividida.length; i++){
                lacunas.push("_")
            }
        setEscolhida(lacunas)
    }
    //Verifica a imagem da forca e erros
    function Erros(){
        let n = 0;
        return(<>
        <img src={forca0} alt="" />
        </>)
    }

    //Renderiza as letras do alfabeto
    function InserirLetras(){
        return (<>
            {alfabeto.map((l, index) => <li key={index} 
            className={`tecladoL ${jogavel}  ${letraS.includes(l) ? "desabilitado" : ""}`} 
            onClick={iniciarJogo ? (() => confereLetras(l)) : (() => 
            alert("Escolha uma Palavra para iniciar o jogo"))}>{l}</li>)}
        </>)
}   
    //INPUT
    function InserirInput(){
        return(<>
        <label>Já sei a palavra!</label>
        <input type="text" value={chuteP} onChange={event => setChuteP(event.target.value)}></input>
        <button className={` ${jogavel}`}
        onClick={iniciarJogo ? (() => FazerChute) : (() => alert("Escolha uma Palavra para iniciar o jogo"))}>Chutar!</button> </>)
    }
    //VERIFICAR O CHUTE
    function FazerChute(){
        let testeChute = [...chuteP];//veriricar como usar o prop console.log(pDividida);
        if(testeChute === pDividida){            
            alert("Você ganhou!")
        } else {
            alert("Voce foi enforcado!")
        }
    }
//Conferir letras
    function confereLetras(l){
        let qteErros = 0;
        if(!letraS.includes(l)){
            const letrasTentadas = [...letraS, l];
            console.log(letrasTentadas);
            setLetrasCertas(letrasTentadas)
            console.log(pDividida);
            /* if(pDividida.includes(l){ver como trazer a pDividida pra cá e como fazer para se a letra
                estiver contida em pDividida fazer ela aparecer na lacuna}) */
        }            
    }
    

    //Renderiza a tela
    return (<div>
    <div className="faixaSuperior">
        <Erros />
        <button onClick={EscolherPalavra}>Escolher Palavra</button>
        <p>{escolhida}</p>
    </div>  
    <ul className="teclado"><InserirLetras /></ul>      
    <div className="chute"><InserirInput /></div>
    </div>)
}
